update.foreach:
update products_tb
set
    sales_count = sales_count + #{item.quantity},
    stock = stock - #{item.quantity}
where
    product_id = #{item.productId}

mapper.select:
select pt.product_id, pt.title, pt.price, pt.description, pt.origin, pt.thumbnail_img, ct.category_id as ct_category_id, ct.name, sct.semi_category_id, sct.name as sct_name from products_tb pt left outer join product_categories_tb pct on(pt.product_id = pct.product_id) left outer join semi_categories_tb sct on (sct.semi_category_id = pct.semi_category_id) left outer join categories_tb ct on (ct.category_id = pct.category_id) where 1=1
and pct.category_id = #{mainCategoryId}
and pct.semi_category_id = #{semiCategoryId}
limit #{startIndex}, #{limit}

select count(*) from categories_tb ct left outer join product_categories_tb pct on (ct.category_id = pct.category_id) left outer join semi_categories_tb sct on (sct.semi_category_id = pct.semi_category_id) left outer join products_tb pt on (pct.product_id = pt.product_id) where 1=1
and ct.category_id = #{mainCategoryId}
and sct.semi_category_id = #{semiCategoryId}
